<div class="col-xs-3 col-md-3 col-lg-3 col-xl-3">
    <a>
        <div style="margin-bottom:10px;"><span><b>Ad Settings </b></span> </div>
        <label class="switch">
            <input id="adSettingsCheckbox" type="checkbox">
            <span class="slider round"></span>
    </a>
</div>
<div id="adSettingsDiv" style="display:none;">
    <hr class="hr-3" style="margin-bottom:15px;">
    <div class="d-flex justify-content-between flex-div-4" style="margin:10px;">
        <div class="col-5 div-col-1">
            <div class="d-flex">*
                <b><input type="text" name="campaign" id="campaign" style="border:none; background:transparent;" value="" placeholder="Campaing Name"></b>
            </div>
        </div>
        <div class="col-5 div-col-1">
            <div class="d-flex">*
                <b><input type="text" name="daily" pattern="\d{11}" title="Sayı giriniz" id="daily" style="border:none; background:transparent;" value="" placeholder="Total Impression Limit"></b>
            </div>
        </div>
    </div>
    <div class="d-flex justify-content-between flex-div-4" style="margin:10px;">
        <div class="col-5 div-col-1">
            <div class="d-flex">*
                <b><input type="text" name="price" id="price" pattern="\d{11}" title="Sayı giriniz"  style="border:none; background:transparent;" value="" placeholder="Unit Price"></b>
            </div>
        </div>
        <div class="col-5 div-col-1">
            <div class="d-flex">*
                <b><input type="text" name="third" id="third" style="border:none; background:transparent;" value="" placeholder="Third Party Pixel"></b>
            </div>
            
        </div>
        
    </div>
    <div class="d-flex justify-content-between flex-div-4" style="margin:10px;">
     
        <div class="col-5 div-col-1">
            <div class="d-flex">*
                <b><input type="text" name="third2" id="third2" style="border:none; background:transparent;" value="" placeholder="Third Party Pixel 2"></b>
            </div>
            
        </div>
    
        
    </div>
    
</div>
