<div class="col-xs-3 col-md-3 col-lg-3 col-xl-3">
    <a>
        <div style="margin-bottom:10px;"><span><b>Video Settings </b></span> </div>
        <label class="switch">
            <input id="videoSettingsCheckbox" type="checkbox">
            <span class="slider round"></span>
    </a>
</div>
<span>
    <div id="videoSettingsDiv" style="display:none;" class="row row-1">
        <span class="span-2"><b>Play Video Speed</b></span>
        <span>
            <div class="d-flex justify-content-between">
                <div class="form-check me-2">
                    <input class="form-check-input me-2" type="radio" name="videoSpeed" id="videoSpeed" value="0.10">
                    <label class="form-check-label label-font-1" for="flexRadioDefault1">
                        0.10
                    </label>
                </div>
                <div class="form-check me-2">
                    <input class="form-check-input me-2" type="radio" name="videoSpeed" id="videoSpeed" value="0.25">
                    <label class="form-check-label label-font-1" for="flexRadioDefault1">
                        0.25
                    </label>
                </div>
                <div class="form-check me-2">
                    <input class="form-check-input me-2" type="radio" name="videoSpeed" id="videoSpeed" value="0.5">
                    <label class="form-check-label label-font-1" for="flexRadioDefault1">
                        0.5
                    </label>
                </div>
                <div class="form-check me-2">
                    <input class="form-check-input me-2" type="radio" name="videoSpeed" id="videoSpeed" value="1.0" checked>
                    <label class="form-check-label label-font-1" for="flexRadioDefault1">
                        1.0
                    </label>
                </div>
            </div>
        </span>
    </div>
